import axios from "axios";
import { createSignature } from "./createSignature";

export async function fileUpload(file) {
  const accessKey = process.env.NCP_ACCESS_KEY;
  const serviceId = process.env.NCP_SMS_SERVICE_ID;

  if (!accessKey || !serviceId) throw new Error("Cannot find essential env");

  const apiUrl = `/sms/v2/services/${serviceId}/files`;
  const date = Date.now().toString();
  const signedKey = createSignature(apiUrl, date);

  //file upload
  const fileResponse = await axios.post(
    `https://sens.apigw.ntruss.com/sms/v2/services/${serviceId}/files`,
    {
      fileName: "qrcode.jpeg",
      fileBody:
        "",
    },
    {
      headers: {
        "Content-Type": "application/json",
        "x-ncp-iam-access-key": accessKey,
        "x-ncp-apigw-timestamp": date,
        "x-ncp-apigw-signature-v2": signedKey,
      },
    }
  );

  return fileResponse;
}
